/**
 * @param theta [0,360]=>[0,65535]
 * @param sine q15, [-1,1]=>[-32768,32767]
 * @param cosine q15, [-1,1]=>[-32768,32767]
 */
void SinCosQ15(u16 theta, s16* sine, s16* cosine)
{
    u8 quadrant = (theta >> 14) & 0x03u;
    u8 offset   = (theta & 0x3FFFu) >> 6;

    switch (quadrant)
    {
        case 0:  // 0~pi/2
            *sine   = aSinCosTbl[offset];
            *cosine = aSinCosTbl[255 - offset];
            break;
        case 1:  // pi/2~pi
            *sine   = aSinCosTbl[255 - offset];
            *cosine = -aSinCosTbl[offset];
            break;
        case 2:  //-pi~-pi/2
            *sine   = -aSinCosTbl[offset];
            *cosine = -aSinCosTbl[255 - offset];
            break;
        case 3:  // -pi/2~0
            *sine   = -aSinCosTbl[255 - offset];
            *cosine = aSinCosTbl[offset];
            break;
    }
}

s16 SinQ15(s16 theta)
{
    u8 quadrant = (theta >> 14) & 0x03u;
    u8 offset   = (theta & 0x3FFFu) >> 6;

    s16 result;

    switch (quadrant)
    {
        case 0:  // 0~pi/2
            result = aSinCosTbl[offset];
            break;
        case 1:  // pi/2~pi
            result = aSinCosTbl[255 - offset];
            break;
        case 2:  //-pi~-pi/2
            result = -aSinCosTbl[offset];
            break;
        case 3:  // -pi/2~0
            result = -aSinCosTbl[255 - offset];
            break;
    }

    return result;
}

s16 CosQ15(s16 theta)
{
    u8 quadrant = (theta >> 14) & 0x03u;
    u8 offset   = (theta & 0x3FFFu) >> 6;

    s16 result;

    switch (quadrant)
    {
        case 0:  // 0~pi/2
            result = aSinCosTbl[255 - offset];
            break;
        case 1:  // pi/2~pi
            result = -aSinCosTbl[offset];
            break;
        case 2:  //-pi~-pi/2
            result = -aSinCosTbl[255 - offset];
            break;
        case 3:  // -pi/2~0
            result = aSinCosTbl[offset];
            break;
    }

    return result;
}

